import React, { useState, useEffect } from "react";
import logoImage from './logo.png';
import './App.css';

const customStyle = {
  background: "#669999",
  margin: "auto -16px",
  padding: "16px 32px",
  fontFamily: "Times New Roman",
  fontWeight: "bold",
  boxShadow: "0 0 10px rgba(0, 0, 0, 0.3)",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  overflow: "hidden" // Ensure overflow is hidden to prevent text from overflowing the header
};

const headername= {
  fontWeight: "bold",
  marginBottom: "10px",
  textShadow: "3px 3px 3px white"
};

const impmsg = {
  animation: "runningText 10s linear infinite",
  alignItems: "center",
  color: "yellow",
  whiteSpace: "nowrap" // Prevent text from wrapping
};

const logoStyle = {
  height: "50px",
  width: "100px"
};

function Header() {
  const [seconds, setSeconds] = useState(60);

  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds(prevSeconds => prevSeconds - 1);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <header style={customStyle}>
      <h1 style={headername}>Aptitude Test</h1>
      <p style={impmsg}>
        Your under our surveillance, So don't try to copy questions or any other malpractices!!! Timer: {seconds}s
      </p>
      <img src={logoImage} alt="Company Logo" style={logoStyle} />
    </header>
  );
}

export default Header;
