import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// Question component
const Question = ({ question, options = [], onAnswer }) => {
  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{question}</h5>
        {options.map((option, index) => (
          <div className="form-check" key={index}>
            <input
              className="form-check-input"
              type="radio"
              name={question}
              value={option}
              id={`${question}-${index}`}
              onChange={(e) => onAnswer(e.target.value)}
            />
            <label className="form-check-label" htmlFor={`${question}-${index}`}>
              {option}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

// Test component
const Test = () => {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const navigate = useNavigate();

  const questions = [
    {
      question: "Q1. An accurate clock shows 8 o'clock in the morning. Through how many degrees will the hour hand rotate when the clock shows 2 o'clock in the afternoon?",
      options: ["144°", "150°", "168°", "180°"],
      correctAnswer: "180°"
    },
    {
      question: "Q2. (476 + 424)2 - 4 x 476 x 424 = ?",
      options: ["2906", "3116", "2104", "2904"],
      correctAnswer: "2904"
    },
    {
      question: "Q3. It was Sunday on Jan 1, 2006. What was the day of the week Jan 1, 2010?",
      options: ["Sunday", "Saturday", "Friday", "Wednesday"],
      correctAnswer: "Friday"
    },
    {
      question: "Q4. The square root of 64009 is:",
      options: ["253", "347", "363", "803"],
      correctAnswer: "253"
    },
   
    // Add more questions as needed
  ];

  const handleAnswer = (questionIndex, answer) => {
    setAnswers({
      ...answers,
      [questionIndex]: answer
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const calculatedScore = questions.reduce((acc, q, index) => {
      return acc + (q.correctAnswer === answers[index] ? 1 : 0);
    }, 0);

    setScore(calculatedScore);
    setSubmitted(true);

    const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage after login
    const testResults = {
      score: calculatedScore,
      answers
    };

    try {
      await axios.post('/api/users/submit-test', { testResults }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Test results submitted successfully');
      localStorage.removeItem('token'); // Remove the token to log the user out
      navigate('/login'); // Redirect to login page
    } catch (error) {
      console.error('Error submitting test results:', error);
      if (error.response && error.response.data) {
        alert(`Failed to submit test results: ${error.response.data.message || 'Unauthorized'}`);
      } else {
        alert('Failed to submit test results. Please try again later.');
      }
    }
  };

  const preventActions = (event) => {
    if (event.type === 'copy' || event.type === 'dragstart' || event.type === 'selectstart') {
      event.preventDefault();
    }
  };

  return (
    <div className="container mt-5" onContextMenu={preventActions} onContextMenuCapture={preventActions} onCopy={preventActions} onDragStart={preventActions} onSelectStart={preventActions}>
      <form onSubmit={handleSubmit}>
        {questions.map((q, index) => (
          <Question
            key={index}
            question={q.question}
            options={q.options}
            onAnswer={(answer) => handleAnswer(index, answer)}
          />
        ))}
        <button type="submit" className="btn btn-primary mt-3">Submit</button>
      </form>
    </div>
  );
};

export default Test;
