import React, { useState } from 'react';
import axios from 'axios';

// Question component
const Question = ({ question, options = [], onAnswer }) => {
  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{question}</h5>
        {options.map((option, index) => (
          <div className="form-check" key={index}>
            <input
              className="form-check-input"
              type="radio"
              name={question}
              value={option}
              id={`${question}-${index}`}
              onChange={(e) => onAnswer(e.target.value)}
            />
            <label className="form-check-label" htmlFor={`${question}-${index}`}>
              {option}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

// Test component
const Test = () => {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const questions = [
    {
      question: "Q1. An accurate clock shows 8 o'clock in the morning. Through how many degrees will the hour hand rotate when the clock shows 2 o'clock in the afternoon?",
      options: ["144°", "150°", "168°", "180°"],
    },
    {
      question: "Q2.{(476 + 424)2 - 4 x 476 x 424} =  ",
      options: ["2906", "3116", "2104", "2904"],
    },
    {
      question: "Q3. It was Sunday on Jan 1, 2006. What was the day of the week Jan 1, 2010?",
      options: ["Sunday", "Saturday", "Friday", "Wednesday"],
    },
    {
      question: "Q4.The square root of 64009 is:",
      options: ["253", "347", "363", "803"],
    },
    // Add more questions as needed
  ];

  const handleAnswer = (questionIndex, answer) => {
    setAnswers({
      ...answers,
      [questionIndex]: answer,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        Authorization: `Bearer ${token}`,
      };

      const userId = 'userIdHere'; // Replace with the actual user's _id
      await axios.post('/api/users/submit-test', { userId, answers }, { headers });
      setSubmitted(true);
    } catch (error) {
      console.error('Failed to submit test result', error);
    }
  };

  const preventActions = (event) => {
    if (event.type === 'copy' || event.type === 'dragstart' || event.type === 'selectstart') {
      event.preventDefault();
    }
  };

  return (
    <div className="container mt-5" onContextMenu={preventActions} onCopy={preventActions} onDragStart={preventActions} onSelectStart={preventActions}>
      <form onSubmit={handleSubmit}>
        {questions.map((q, index) => (
          <Question
            key={index}
            question={q.question}
            options={q.options}
            onAnswer={(answer) => handleAnswer(index, answer)}
          />
        ))}
        <button type="submit" className="btn btn-primary mt-3">Submit</button>
      </form>
      {submitted && (
        <div className="alert alert-success mt-4">
          <strong>Test submitted successfully!</strong>
        </div>
      )}
    </div>
  );
};

export default Test;
