import React, { useState, useEffect } from "react";
import logoImage from './logo.png';
import './App.css';

const customStyle = {
  background: "#669999",
  margin: "auto -16px",
  padding: "16px 32px",
  fontFamily: "Times New Roman",
  fontWeight: "bold",
  boxShadow: "0 0 10px rgba(0, 0, 0, 0.3)",
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  overflow: "hidden" // Ensure overflow is hidden to prevent text from overflowing the header
};

const headername= {
  fontWeight: "bold",
  marginBottom: "10px",
  textShadow: "3px 3px 3px white"
};

const impmsg = {
  animation: "runningText 10s linear infinite",
  alignItems: "center",
  color: "red",
  textShadow: " 1px 1px 1px black",
  whiteSpace: "nowrap" // Prevent text from wrapping
};

const logoStyle = {
  height: "50px",
  width: "100px"
};

function Header() {
  const initialTime = 2 * 60 * 60; // Initial time in seconds (2 hours)
  const [seconds, setSeconds] = useState(initialTime);

  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds(prevSeconds => {
        if (prevSeconds <= 0) {
          clearInterval(interval);
          return 0;
        }
        return prevSeconds - 1;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const formatTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  };

  return (
    <header style={customStyle}>
      <h1 style={headername}>Aptitude Test</h1>
      <p style={impmsg}>
        *You are under our surveillance, so don't try to copy questions or any other malpractices!!! Timer: {formatTime(seconds)}
      </p>
      <img src={logoImage} alt="Company Logo" style={logoStyle} />
    </header>
  );
}

export default Header;
