import React, { useState } from 'react';
import axios from 'axios';

const Question = ({ question, options = [], onAnswer }) => {
  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{question}</h5>
        {options.map((option, index) => (
          <div className="form-check" key={index}>
            <input
              className="form-check-input"
              type="radio"
              name={question}
              value={option}
              id={`${question}-${index}`}
              onChange={(e) => onAnswer(e.target.value)}
            />
            <label className="form-check-label" htmlFor={`${question}-${index}`}>
              {option}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

const Test = () => {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);

  const questions = [
    // Your questions array
  ];

  const handleAnswer = (questionIndex, answer) => {
    setAnswers({
      ...answers,
      [questionIndex]: answer
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const calculatedScore = questions.reduce((acc, q, index) => {
      return acc + (q.correctAnswer === answers[index] ? 1 : 0);
    }, 0);

    setScore(calculatedScore);
    setSubmitted(true);

    const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage after login
    const testResults = {
      score: calculatedScore,
      answers
    };

    try {
      await axios.post('/api/users/submit-test', { testResults }, {
        headers: {
          'Authorization': token
        }
      });
      alert('Test results submitted successfully');
    } catch (error) {
      console.error('Error submitting test results:', error);
      alert('Failed to submit test results');
    }
  };

  const preventActions = (event) => {
    if (event.type === 'copy' || event.type === 'dragstart' || event.type === 'selectstart') {
      event.preventDefault();
    }
  };

  return (
    <div className="container mt-5" onContextMenu={preventActions} onCopy={preventActions} onDragStart={preventActions} onSelectStart={preventActions}>
      <form onSubmit={handleSubmit}>
        {questions.map((q, index) => (
          <Question
            key={index}
            question={q.question}
            options={q.options}
            onAnswer={(answer) => handleAnswer(index, answer)}
          />
        ))}
        <button type="submit" className="btn btn-primary mt-3">Submit</button>
      </form>
      {submitted && (
        <div className="alert alert-success mt-4">
          <strong>You've completed the test successfully! Your score is {score}.</strong>
        </div>
      )}
    </div>
  );
};

export default Test;
