import React, { useState } from 'react';

// Question component
const Question = ({ question, options = [], onAnswer }) => {
  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{question}</h5>
        {options.map((option, index) => (
          <div className="form-check" key={index}>
            <input
              className="form-check-input"
              type="radio"
              name={question}
              value={option}
              id={`${question}-${index}`}
              onChange={(e) => onAnswer(e.target.value)}
            />
            <label className="form-check-label" htmlFor={`${question}-${index}`}>
              {option}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

// Test component
const Test = () => {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const questions = [
    {
      question: "Q1. What is the capital of France?",
      options: ["Berlin", "Madrid", "Paris", "Lisbon"],
      correctAnswer: "Paris"
    },
    {
      question: "Q2. What is 2 + 2?",
      options: ["3", "4", "5", "6"],
      correctAnswer: "4"
    },
    {
      question: "Q3. It was Sunday on Jan 1, 2006. What was the day of the week Jan 1, 2010?",
      options: ["Sunday", "Saturday", "Friday", "Wednesday"],
      correctAnswer: "Friday"
    }
  ];

  const handleAnswer = (questionIndex, answer) => {
    setAnswers({
      ...answers,
      [questionIndex]: answer // Use the question's index to associate the answer
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  const preventActions = (event) => {
    if (/*event.type === 'contextmenu' ||*/ event.type === 'copy' || event.type === 'dragstart' || event.type === 'selectstart') {
      event.preventDefault();
    }
  };

  return (
    <div className="container mt-5" onContextMenu={preventActions} onCopy={preventActions} onDragStart={preventActions} onSelectStart={preventActions}>
      <form onSubmit={handleSubmit}>
        {questions.map((q, index) => (
          <Question
            key={index}
            question={q.question}
            options={q.options}
            onAnswer={(answer) => handleAnswer(index, answer)} // Pass the question's index and the answer
          />
        ))}
        <button type="submit" className="btn btn-primary mt-3">Submit</button>
      </form>
      {submitted && (
        <div className="alert alert-success mt-4">
          <strong>You've completed the test successfully!</strong>
        </div>
      )}
    </div>
  );
};

export default Test;
