import React, { useState } from 'react';
import Question from './Question'; // Adjust the path if needed

const questions = [
  {
    question: "Q1. What is the capital of France?",
    options: ["Berlin", "Madrid", "Paris", "Lisbon"],
    correctAnswer: "Paris"
  },
  {
    question: "Q2. What is 2 + 2?",
    options: ["3", "4", "5", "6"],
    correctAnswer: "4"
  },
  {
    question: "Q3. It was Sunday on Jan 1, 2006. What was the day of the week Jan 1, 2010?",
    options: ["Sunday", "Saturday", "Friday", "Wednesday"],
    correctAnswer: "Friday"
  },
];

const Test = () => {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const handleAnswer = (question, answer) => {
    setAnswers({
      ...answers,
      [question]: answer
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  const preventActions = (event) => {
    if (event.type === 'contextmenu' || event.type === 'copy' || event.type === 'dragstart' || event.type === 'selectstart') {
      event.preventDefault();
    }
  };

  return (
    <div className="container mt-5" onContextMenu={preventActions} onCopy={preventActions} onDragStart={preventActions} onSelectStart={preventActions}>
      <form onSubmit={handleSubmit}>
        {questions.map((q, index) => (
          <Question
            key={index}
            question={q.question}
            options={q.options}
            onAnswer={(answer) => handleAnswer(q.question, answer)}
          />
        ))}
        <button type="submit" className="btn btn-primary mt-3">Submit</button>
      </form>
      {submitted && (
        <div className="alert alert-success mt-4">
          <strong>You've completed the test successfully!</strong>
        </div>
      )}
    </div>
  );
};

export default Test;
